import * as SQLite from 'expo-sqlite';

const db = SQLite.openDatabase("db.salesApp");

const initDB = () => {
  db.transaction(tx => {
    tx.executeSql(
      "CREATE TABLE IF NOT EXISTS products (id INTEGER PRIMARY KEY AUTOINCREMENT, code TEXT, description TEXT, price REAL, category TEXT);"
    );
    tx.executeSql(
      "CREATE TABLE IF NOT EXISTS categories (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT);"
    );
    tx.executeSql(
      "CREATE TABLE IF NOT EXISTS sales (id INTEGER PRIMARY KEY AUTOINCREMENT, saleCode TEXT, saleDate TEXT);"
    );
    tx.executeSql(
      "CREATE TABLE IF NOT EXISTS sale_items (id INTEGER PRIMARY KEY AUTOINCREMENT, saleId INTEGER, productId INTEGER, quantity INTEGER, FOREIGN KEY(saleId) REFERENCES sales(id), FOREIGN KEY(productId) REFERENCES products(id));"
    );
  });
}

export { initDB };
