// src/telas/Home/HomeScreen.js
import React from 'react';
import { View, Text, Button, ScrollView } from 'react-native';
import styles from './styles';

const HomeScreen = ({ navigation }) => {
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>Vendinha do Vinycius</Text>
      <View style={styles.buttonContainer}>
        <Button
          title="CRUD Produtos"
          onPress={() => navigation.navigate('CrudProdutos')}
        />
        <Button
          title="Vender"
          onPress={() => navigation.navigate('Vender')}
        />
        <Button
          title="Carrinho"
          onPress={() => navigation.navigate('Carrinho')}
        />
        <Button
          title="Todas as Vendas"
          onPress={() => navigation.navigate('TodasVendas')}
        />
        <Button
          title="CRUD Categorias"
          onPress={() => navigation.navigate('CrudCategorias')}
        />
        <Button
          title="Sobre"
          onPress={() => navigation.navigate('Sobre')}
        />
      </View>
    </ScrollView>
  );
};

export default HomeScreen;
